  private void calcButActionPerformed(java.awt.event.ActionEvent evt)//GEN-FIRST:event_calcButActionPerformed
  {//GEN-HEADEREND:event_calcButActionPerformed
    // TODO add your handling code here:
    String inText = inputArea.getText();
    Scanner input = new Scanner(inText);
    int n = 1;
    double sum = 0;
    double mean = 0;
    
    double numSqrdSum = 0;
    double min = input.nextDouble();
    double max = min;
    sum+=min;
    numSqrdSum+=Math.pow(min,2);
    while(input.hasNext()){
      n++;
      double inInt = input.nextDouble();
      sum+=inInt;
      numSqrdSum+=Math.pow(inInt,2);
      if(inInt<min)
        min = inInt;
      if(inInt>max)
        max = inInt;
    }
    mean = sum/n;
    double sumSqrd = Math.pow(sum,2);
    double standDev = Math.sqrt(((double)(1)/(n-1))*(numSqrdSum-(((1)/((double)(n)))*sumSqrd)));
    String outputString = "          n = "+n+"\n        sum = "+sum+"\n       mean = "+mean+"\n          Ïƒ = "+standDev+"\n    minimum = "+min+"\n    maximum = "+max;
    outputArea.setText(outputString);
  }//GEN-LAST:event_calcButActionPerformed


